CREATE TABLE IF NOT EXISTS `product_chatbot_queries` (`id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT, `user_id` BIGINT UNSIGNED NOT NULL, `product_id` BIGINT UNSIGNED NOT NULL, `question` TEXT NOT NULL, `answer` TEXT NULL, `is_auto_answer` TINYINT(1) NOT NULL DEFAULT 0, `status` ENUM('pending', 'answered', 'closed') NOT NULL DEFAULT 'pending', `language` VARCHAR(10) NOT NULL DEFAULT 'en', `answered_by` BIGINT UNSIGNED NULL, `answered_at` TIMESTAMP NULL, `created_at` TIMESTAMP NULL, `updated_at` TIMESTAMP NULL, PRIMARY KEY (`id`), KEY `product_chatbot_queries_product_id_status_index` (`product_id`, `status`), KEY `product_chatbot_queries_user_id_product_id_index` (`user_id`, `product_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci; CREATE TABLE IF NOT EXISTS `product_faqs` (`id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT, `product_id` BIGINT UNSIGNED NULL, `question` VARCHAR(255) NOT NULL, `answer` TEXT NOT NULL, `language` VARCHAR(10) NOT NULL DEFAULT 'en', `priority` INT NOT NULL DEFAULT 0, `is_active` TINYINT(1) NOT NULL DEFAULT 1, `created_at` TIMESTAMP NULL, `updated_at` TIMESTAMP NULL, PRIMARY KEY (`id`), KEY `product_faqs_product_id_language_is_active_index` (`product_id`, `language`, `is_active`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci; CREATE TABLE IF NOT EXISTS `translations` (`id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT, `source_text` TEXT NOT NULL, `target_language` VARCHAR(10) NOT NULL, `translated_text` TEXT NOT NULL, `usage_count` INT NOT NULL DEFAULT 0, `created_at` TIMESTAMP NULL, `updated_at` TIMESTAMP NULL, PRIMARY KEY (`id`), UNIQUE KEY `translations_source_text_target_language_unique` (`source_text`(255), `target_language`), KEY `translations_target_language_index` (`target_language`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci; CREATE TABLE IF NOT EXISTS `live_streams` (`id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT, `user_id` BIGINT UNSIGNED NOT NULL, `channel_name` VARCHAR(255) NOT NULL, `title` VARCHAR(255) NOT NULL, `description` TEXT NULL, `status` ENUM('live', 'ended', 'scheduled') NOT NULL DEFAULT 'live', `viewer_count` INT NOT NULL DEFAULT 0, `likes_count` INT NOT NULL DEFAULT 0, `started_at` TIMESTAMP NULL, `ended_at` TIMESTAMP NULL, `created_at` TIMESTAMP NULL, `updated_at` TIMESTAMP NULL, PRIMARY KEY (`id`), UNIQUE KEY `live_streams_channel_name_unique` (`channel_name`), KEY `live_streams_user_id_index` (`user_id`), KEY `live_streams_status_index` (`status`), KEY `live_streams_channel_name_index` (`channel_name`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci; CREATE TABLE IF NOT EXISTS `stream_products` (`id` BIGINT UNSIGNED NOT NULL AUTO_INCREMENT, `stream_id` BIGINT UNSIGNED NOT NULL, `product_id` BIGINT UNSIGNED NOT NULL, `display_order` INT NOT NULL DEFAULT 0, `is_featured` TINYINT(1) NOT NULL DEFAULT 0, `added_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP, `created_at` TIMESTAMP NULL, `updated_at` TIMESTAMP NULL, PRIMARY KEY (`id`), UNIQUE KEY `stream_products_stream_id_product_id_unique` (`stream_id`, `product_id`), KEY `stream_products_stream_id_index` (`stream_id`), KEY `stream_products_product_id_index` (`product_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;



